package mesquite.examples.ExTreeUtility;/*~~  */import mesquite.lib.*;import mesquite.lib.duties.*;/** ======================================================================== */public class ExTreeUtility extends TreeUtility {	/*.................................................................................................................*/	public boolean startJob(String arguments, Object condition,  boolean hiredByName) {		return true;   	} 	 	 	/* This is the focal method of a TreeUtility.  The tree is not meant to be modified (use subclass of TreeTransformer for that).  Rather, 	The tree could be exported, emailed, printed, used for an instant calculations, and so on.  This is not intended to be persistent for a tree window. 	These appear within the Utilities submenu of the Tree menu of the Tree window */	public  void useTree(Tree tree) {				logln("Tree " + tree.writeTree());			}		/*.................................................................................................................*/    	 public String getName() {		return "Write tree to Log File";   	 }	/*.................................................................................................................*/ 	/** returns an explanation of what the module does.*/ 	public String getExplanation() { 		return "Puts the text description of the tree into the log.  This is an example module to show how to program for Mesquite.";   	 }   	 }