package mesquite.examples.ExNumberForTree;import mesquite.lib.*;import mesquite.lib.duties.*;public class ExNumberForTree extends NumberForTree {	public boolean startJob(String arguments, Object condition, CommandRecord commandRec, boolean hiredByName) { 		return true;	}	  	 	/** (called in case this module needs to initialize anything; this module doesn't) */   	public void initialize(Tree tree, CommandRecord commandRec){   	}   		/** This is the focal method of a NumberForTree.  A tree is passed; the tree should not be modified!!!!	The number is returned in result; a description of the result can be returned in resultString.	This particular module does a simple quantification of how resolved is the tree.*/	public void calculateNumber(Tree tree, MesquiteNumber result, MesquiteString resultString, CommandRecord commandRec) {    	 	if (result==null || tree == null)    	 		return;    	 	double resolutionIndex = (1.0*tree.numberOfInternalsInClade(tree.getRoot()))/(tree.numberOfTerminalsInClade(tree.getRoot())-1);		result.setValue(resolutionIndex);		if (resultString!=null)			resultString.setValue("Resolution index: "+ result.toString());	}		/** Explains what the module does.*/    	 public String getExplanation() {		return "Calculates the resolution index for a tree.  This is an example module to show how to program for Mesquite.";   	 }   	 	/** Name of module*/    	 public String getName() {		return "Resolution Index";   	 }}